.cuadroMensaje{
  border: solid gray 1px;
  border-radius: 20px;
  padding: 2px;
  margin: 10px;
}
.contentMessage{
  overflow: scroll;
  height: 600px;
}
::-webkit-scrollbar {
  width: 10px;
}

/* Track */
::-webkit-scrollbar-track {

  border-radius: 10px;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: gray;
  border-radius: 10px;
}
