import fire from '@/fb.js'
export default {
  data () {
    return {
      dialog: false,
      nameTeam: '',
      numberPlayers: null
    }
  },
  methods: {
    enviarTeam () {
      const team = {
        name_team: this.nameTeam,
        number_players: this.numberPlayers
      }
      fire.db.collection('teams').add(team).then(() => {
        console.log('Este team a sido añadido')
      })
      this.dialog = false
    }
  }
}
