      fire.auth.createUserWithEmailAndPassword(this.mail, this.pass)
        .then(res => {
          console.log(res)
          this.user = res.user.email
          this.$store.commit('userLogged', this.user)
        }).catch(function (error) {
          // Handle Errors here.
          console.log(error.code)
          console.log(error.message)
        })
    },
