        v => !!v || 'Name is required',
        v => (v && v.length <= 10) || 'Name must be less than 10 characters'
      ],
      team1: '',
      team2: '',
      place: '',
      date: {
        day: null,
        month: null
      }
    }
  },
  methods: {
    enviarMatch () {
      const match = {
        team1: this.team1,
