    validate () {
      if (this.$refs.form.validate()) {
        this.snackbar = true
      }
      console.log('hola')
    },
    reset () {
      this.$refs.form.reset()
    }
  },
  computed: {
    dias () {
      let totalDays = []
      for (let i = 1; i < 32; i++) {
        totalDays.push(i)
      }
      return totalDays
    },
    meses () {
      let allMonths = []
      for (var i = 1; i < 13; i++) {
        allMonths.push(i)
      }
      return allMonths
    }
  }
